<template>  <div class="news-item">    <div class="news-item__title">      {{title}}    </div>    <div class="news-item__img">      <img :src="img" alt="">    </div>    <div class="news-item__description">      {{news_des}}    </div>    <div class="news-item__url">      {{url}}    </div>  </div></template><script>  export default {    name: "news-item",    props: {      title: {        type: String,        default: ''      },      img: {        type: String,        default: ''      },      news_des: {        type: String,        default: ''      },      url: {        type: String,        default: ''      },    }  }</script><style lang="scss" scoped>.news{  &-item{    flex: 0 0 40%;    &__title{      font-size: 23px;      font-weight: bold;      margin-bottom: 30px;    }     &__img{       img{         width: auto;         height: auto;         min-width: 100%;         min-height: 100%;         max-height: 500px;       }     }  }}</style>